import React, { useState } from 'react'
import { file1 } from '../../Images/2011'
import './Gallery.css'
const Gallery1 = () => {
    const [slide, setSlide] = useState(0)
    const [openimg, setOpenimg] = useState(false)

    const handleOpen = (index) => {
        setSlide(index)
        setOpenimg(true)
    }
    const handlePrevious = () => {
        slide === 0 ? setSlide(file1.length - 1) : setSlide(slide - 1)
    }
    const handleNext = () => {
        slide === file1.length - 1 ? setSlide(0) : setSlide(slide + 1)
    }

    return (
        <div>
            <h1 style={{ textAlign: 'center' }}>EEL 2011</h1>
            {
                openimg &&
                <div className='slider'>

                    <button onClick={() => setOpenimg(false)}>X</button>
                    <i class="fa-solid fa-angle-left" onClick={handlePrevious}></i>
                    <i class="fa-solid fa-circle-chevron-right" onClick={handleNext}></i>

                    <div className='slider-singleimg'>
                        <img src={file1[slide].src} alt='' />
                    </div>
                </div>
            }
            <div className='gallery'>
                {
                    file1.map((img, index) => {
                        return (
                            <div key={index} className='images'>
                                <img src={img.src} alt='error' onClick={() => handleOpen(index)} />
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Gallery1
