import React, { useState } from 'react'
import { file1 } from '../../Images/2011'
import Popup from 'reactjs-popup';
import './Gallery.css'
const Gallery1 = () => {
    const [slide, setSlide] = useState(0)
    const [openimg, setOpenimg] = useState(false)

    const handleOpen = (index) => {
        setSlide(index)
        setOpenimg(true)
    }
    const handlePrevious = () => {
        slide === 0 ? setSlide(file1.length - 1) : setSlide(slide - 1)
    }
    const handleNext = () => {
        slide === file1.length - 1 ? setSlide(0) : setSlide(slide + 1)
    }

    return (
        <div className={openimg && 'openimg'}>
            <h1 style={{ textAlign: 'center' }}>EEL 2011</h1>
            {
                openimg ?
                    <Popup trigger={<button><img src={file1[slide].src} alt='' /></button>} position="center">
                        <div className='slider'>

                            <div>
                                <button className='close' onClick={() => setOpenimg(false)}>x</button>
                                <button className='btn-prev' onClick={handlePrevious}>&lt;</button>
                                <button className='btn-next' onClick={handleNext}>&gt;</button>
                            </div>

                        </div>
                    </Popup> :
                    <div className='gallery'>
                        {
                            file1.map((img, index) => {
                                return (
                                    <div key={index} className='images'>
                                        <img src={img.src} alt='error' onClick={() => handleOpen(index)} />
                                    </div>
                                )
                            })
                        }
                    </div>
            }

        </div>
    )
}

export default Gallery1
